import React, { useState } from "react";
import "./Users.css";  // Import the CSS file for styling

const Users = () => {
  // Initial list of problems
  const [problems, setProblems] = useState([
    { id: 1, name: "Two Sum", description: "Find two numbers that add up to a target number." },
    { id: 2, name: "Reverse String", description: "Reverse a given string." },
  ]);

  // State to manage new problem entry
  const [newProblem, setNewProblem] = useState({ name: "", description: "" });

  // Function to handle input changes for the new problem form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProblem({ ...newProblem, [name]: value });
  };

  // Function to add a new problem
  const handleAddProblem = () => {
    setProblems([
      ...problems,
      { id: problems.length + 1, name: newProblem.name, description: newProblem.description },
    ]);
    setNewProblem({ name: "", description: "" });
  };

  // Function to delete a problem
  const handleDeleteProblem = (id) => {
    setProblems(problems.filter((problem) => problem.id !== id));
  };

  return (
    <div className="users-container">
      <h1>Problems</h1>
      <table className="problems-table">
        <thead>
          <tr>
            <th>Problem Name</th>
            <th>Problem Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {problems.map((problem) => (
            <tr key={problem.id}>
              <td>{problem.name}</td>
              <td>{problem.description}</td>
              <td>
                <button className="delete-button" onClick={() => handleDeleteProblem(problem.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Add New Problem</h2>
      <div className="new-problem-form">
        <input
          type="text"
          name="name"
          placeholder="Problem Name"
          value={newProblem.name}
          onChange={handleInputChange}
          className="input-field"
        />
        <input
          type="text"
          name="description"
          placeholder="Problem Description"
          value={newProblem.description}
          onChange={handleInputChange}
          className="input-field"
        />
        <button className="add-button" onClick={handleAddProblem}>Add Problem</button>
      </div>
    </div>
  );
};

export default Users;
